import React from "react";
import styled from "styled-components";

const Leaders = () => {
    return (
        <Wrapper>
            <Title>Our team</Title>
            <WrapperLeader>
                <Leader>
                    <StyledMemberItem>
                        <WrapperIfo>
                            <FlexItem>
                                <Name></Name>
                                <p style={{ marginLeft: "-18px" }}>
                                    CO - Founder
                                </p>
                                <p style={{ marginLeft: "-13px" }}>
                                    Lead Blockchain Engineer & Architect
                                </p>
                                <p></p>
                                <Social>
                                    <a
                                        href="https://twitter.com/barodahoang"
                                        target="_blank"
                                        rel="noopener noreferrer"
                                    >
                                        <i className="fab fa-twitter"></i>
                                    </a>
                                    <a
                                        href="https://www.linkedin.com/in/barodahoang/"
                                        target="_blank"
                                        rel="noopener noreferrer"
                                    >
                                        <i className="fab fa-linkedin"></i>
                                    </a>
                                </Social>
                            </FlexItem>
                            <Thumb>
                                <img
                                    src="../images/contributor-page/baroda.png"
                                    alt="icon member"
                                />
                            </Thumb>
                        </WrapperIfo>
                        <Background>
                            <img
                                src="../images/contributor-page/orbit-background.svg"
                                alt="background-orbit-image"
                            />
                        </Background>
                    </StyledMemberItem>
                    <Introduction>
                        <div className="name">Baroda Hoang</div>
                        <div className="content">
                            Before entering the crypto space. He is an expert in
                            marketing and market research. He has been involved
                            in the digital asset ecosystem since 2016 and has
                            worked with and advised a variety of blockchain
                            projects. When participate in some Blockchain events
                            in the Asian market He sees a huge demand for the
                            Blockchain space especially the decentralized
                            financial market.
                        </div>
                    </Introduction>
                </Leader>
                <Leader>
                    <StyledMemberItem>
                        <WrapperIfo>
                            <FlexItem>
                                <Name></Name>
                                <p style={{ marginLeft: "-18px" }}>
                                    CO - Founder
                                </p>
                                <p style={{ marginLeft: "-13px" }}>
                                    Head Strategist
                                </p>
                                <p></p>
                                <Social>
                                    <a
                                        href="https://twitter.com/MenardoQuinosa"
                                        target="_blank"
                                        rel="noopener noreferrer"
                                    >
                                        <i className="fab fa-twitter"></i>
                                    </a>
                                    <a
                                        href="https://www.linkedin.com/in/menardo-quinosa-823498222"
                                        target="_blank"
                                        rel="noopener noreferrer"
                                    >
                                        <i className="fab fa-linkedin"></i>
                                    </a>
                                </Social>
                            </FlexItem>
                            <Thumb>
                                <img
                                    src="../images/contributor-page/mernado.png"
                                    alt="icon member"
                                />
                            </Thumb>
                        </WrapperIfo>
                        <Background>
                            <img
                                src="../images/contributor-page/orbit-background.svg"
                                alt=""
                            />
                        </Background>
                    </StyledMemberItem>
                    <Introduction>
                        <div className="name">Menardo Quiñosa</div>
                        <div className="content">
                            He is a financial strategist, fund manager, analyst
                            and currency trader. He has more than 9 years of
                            experience in FINANCIAL MARKET. He also participated
                            in multiple trading and crypto currency events in
                            Dubai,Thailand, Vietnam, Singapore and Hongkong.
                        </div>
                    </Introduction>
                </Leader>
            </WrapperLeader>
            <ViceLeader>
                <StyledMemberItem>
                    <WrapperIfo>
                        <FlexItem>
                            <Name></Name>
                            <p style={{ marginLeft: "-18px" }}>
                                Head Strategist
                            </p>
                            <p style={{ marginLeft: "-13px" }}>
                                Business Development
                            </p>
                            <p></p>
                            <Social>
                                <a
                                    href="https://www.linkedin.com/in/stella-vo-276627222"
                                    target="_blank"
                                    rel="noopener noreferrer"
                                >
                                    <i className="fab fa-linkedin"></i>
                                </a>
                            </Social>
                        </FlexItem>
                        <Thumb>
                            <img
                                src="../images/contributor-page/stella-vo.png"
                                alt="icon member"
                            />
                        </Thumb>
                    </WrapperIfo>
                    <Background>
                        <img
                            src="../images/contributor-page/orbit-background.svg"
                            alt=""
                        />
                    </Background>
                </StyledMemberItem>
                <Introduction className="vice-leader">
                    <div className="name">Stella Vo</div>
                    <div className="content">
                        He is an economist, he join space blockchain in early
                        2018. He has attended many seminars, given presentations
                        on blockchain, cryptocurrency in many different
                        countries. He belief the blockchain will opportunity the
                        innovations necessary to usher and decentralized trading
                        for all also the future of the whole world.
                    </div>
                </Introduction>
            </ViceLeader>
        </Wrapper>
    );
};
