import React, { useRef, useState } from "react";
import styled from "styled-components";

const ContactUs = () => {
    const [x, setX] = useState();
    const [y, setY] = useState();
    const buttonRef = useRef();

    const getMouseOver = (e) => {
        var parentOffset = buttonRef.current.getBoundingClientRect();
        setX(e.pageX - parentOffset.left);
        setY(e.pageY - parentOffset.top);
    };

    return (
        <BoxSection>
            <WrapContainer>
                <Header>Contact Us.</Header>
                <WrapperSubmit>
                    <WrapperInput>
                        <input type="text" placeholder="Your Name" />
                    </WrapperInput>
                    <WrapperInput>
                        <input type="text" placeholder="Your Email" />
                    </WrapperInput>
                    <WrapperInput>
                        <input type="text" placeholder="Who Are You" />
                    </WrapperInput>
                    <WrapperMessage>
                        <textarea
                            name="text"
                            rows="14"
                            cols="100%"
                            wrap="soft"
                            placeholder="Write your message here"
                        ></textarea>
                    </WrapperMessage>
                    <WrapperButton>
                        <SubmitButton
                            ref={buttonRef}
                            onMouseMove={
                                (e) => getMouseOver(e) //e.clientY - e.target.offsetTop
                            }
                            onMouseLeave={(e) => getMouseOver(e)}
                            // x={x}
                            // y={y}
                        >
                            Send{" "}
                            <span
                                style={{ top: y + "px", left: x + "px" }}
                            ></span>{" "}
                        </SubmitButton>
                    </WrapperButton>
                </WrapperSubmit>
            </WrapContainer>
        </BoxSection>
    );
};

const BoxSection = styled.section`
    position: relative;
    padding: 8vh 0 0;
    margin: 0 0 2rem;
    @media (max-width: 770px) {
        padding-top: 60px;
        margin: 0 0 1rem;
    }
`;
const WrapContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    flex-direction: column;
    gap: 20px;
    @media (max-width: 576px) {
    }
`;
const Header = styled.div`
    font-size: 56px;
    font-weight: 800;
`;
const WrapperSubmit = styled.div`
    padding: 1rem;
    width: 100%;
    max-width: 650px;
    /* display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center; */
    input,
    textarea {
        width: 100%;
        background-color: transparent;
        border: none;
        color: #fff;
        ::placeholder {
            color: #ffffff50;
        }
        :focus-visible {
            outline: none;
        }
    }
`;
const WrapperInput = styled.div`
    width: 100%;
    padding: 1rem;
    margin: 10px auto;
    border: 1px solid;
    border-radius: 10px;
`;
const WrapperMessage = styled.div`
    max-width: 650px;
    padding: 1rem;
    margin: 10px auto;
    border: 1px solid;
    border-radius: 10px;

    textarea {
        max-width: 100%;
        word-wrap: break-word;
        word-break: break-all;
        height: 80px;
    }
`;
const WrapperButton = styled.div`
    display: flex;
    align-items: center;
    justify-content: flex-start;
    padding: 1rem 0;
`;
const SubmitButton = styled.div`
    color: #fff;
    position: relative;
    display: block;
    overflow: hidden;
    width: 100%;
    height: auto;
    max-width: 250px;
    /* max-height: 64px; */
    border-radius: 8px;
    /* margin: 1rem auto; */
    font: normal 18px/60px "proxima-nova", sans-serif;
    text-align: center;
    text-decoration: none;
    border: 2px solid #fff;

    :hover {
        color: #000000;
    }

    span {
        position: absolute;
        /* left: ${({ x }) => x}px; */
        /* top: ${({ y }) => y}px; */

        display: block;
        width: 0;
        height: 0;
        border-radius: 50%;
        background-color: #fff;
        -webkit-transition: width 0.4s ease-in-out, height 0.4s ease-in-out;
        transition: width 0.4s ease-in-out, height 0.4s ease-in-out;
        -webkit-transform: translate(-50%, -50%);
        transform: translate(-50%, -50%);
        z-index: -1;
    }
    :hover span {
        width: 562.5px;
        height: 562.5px;
    }
`;

export default ContactUs;
