import React from "react";
import styled from "styled-components";

const ContactUs = () => {
    return (
        <BoxSection>
            <WrapContainer>
                <Header>Contact Us.</Header>
                <WrapperSubmit>
                    <WrapperInput>
                        <input type="text" placeholder="Your Name" />
                    </WrapperInput>
                    <WrapperInput>
                        <input type="text" placeholder="Your Email" />
                    </WrapperInput>
                    <WrapperInput>
                        <input type="text" placeholder="Who Are You" />
                    </WrapperInput>
                    <WrapperMessage>
                        <textarea
                            name="text"
                            rows="14"
                            cols="100%"
                            wrap="soft"
                            placeholder="Write your message here"
                        >
                            {" "}
                        </textarea>
                    </WrapperMessage>
                    <WrapperButton>
                        <SubmitButton>Send</SubmitButton>
                    </WrapperButton>
                </WrapperSubmit>
            </WrapContainer>
        </BoxSection>
    );
};

const BoxSection = styled.section`
    position: relative;
    padding: 8vh 0 0;
    margin: 0 0 2rem;
    @media (max-width: 770px) {
        padding-top: 60px;
        margin: 0 0 1rem;
    }
`;
const WrapContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    flex-direction: column;
    gap: 20px;
    @media (max-width: 576px) {
    }
`;
const Header = styled.div`
    font-size: 56px;
    font-weight: 800;
`;
const WrapperSubmit = styled.div`
    padding: 1rem;
    width: 100%;
    max-width: 650px;
    /* display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center; */
    input,
    textarea {
        width: 100%;
        background-color: transparent;
        border: none;
        color: #fff;
        ::placeholder {
            color: #ffffff50;
        }
        :focus-visible {
            outline: none;
        }
    }
`;
const WrapperInput = styled.div`
    width: 100%;
    padding: 1rem;
    margin: 10px auto;
    border: 1px solid;
    border-radius: 12px;
`;
const WrapperMessage = styled.div`
    max-width: 650px;
    padding: 1rem;
    margin: 10px auto;
    border: 1px solid;
    border-radius: 12px;

    textarea {
        max-width: 100%;
        word-wrap: break-word;
        word-break: break-all;
        height: 80px;
    }
`;
const WrapperButton = styled.div`
    display: flex;
    align-items: center;
    justify-content: flex-start;
    padding: 1rem 0;
`;
const SubmitButton = styled.div`
    width: fit-content;

    padding: 1rem 1.5rem;
    text-align: center;
    border: 1px solid;
    border-radius: 12px;
    border: 1px solid;
`;

export default ContactUs;
