import React from "react";
import styled from "styled-components";

const Brands = () => {
    const forbitspaceLogo = [
        { number: "01" },
        { number: "02" },
        { number: "03" },
        { number: "04" },
        { number: "05" },
        { number: "06" },
        { number: "07" },
        { number: "08" },
        { number: "09" },
        { number: "10" },
        { number: "11" },
        { number: "12" },
        { number: "13" },
        { number: "14" },
    ];
    const forbitspaceLogoNoise = [
        { number: "02" },
        { number: "03" },
        { number: "04" },
        { number: "05" },
        { number: "06" },
        { number: "07" },
        { number: "08" },
        { number: "09" },
        { number: "10" },
        { number: "11" },
    ];
    return (
        <BoxSection>
            <WrapContainer className="container">
                <WrapImg>
                    <img
                        src="../images/home-page/space-header.png"
                        alt="header-brand-page-image"
                    />
                </WrapImg>
                <Content>
                    <Title>Forbitspace Logo</Title>
                    <WrapperLogo>
                        {forbitspaceLogo.map((item, index) => (
                            <BrandLogo>
                                <BackgroundImg>
                                    <img
                                        src="../images/contributor-page/orbit-background.svg"
                                        alt=""
                                    />
                                </BackgroundImg>
                                <LogoImg>
                                    <img
                                        src={`../images/brand-logo/brandkit-forbit/forbitspace/PNG/Logo-forbitspace-brandkit-${item.number}.png`}
                                        alt=""
                                    />
                                </LogoImg>
                                <LinkImg>
                                    <a
                                        href={`http://localhost:4000/images/brand-logo/brandkit-forbit/forbitspace/SVG/Logo-forbitspace-brandkit-${item.number}.svg`}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                    >
                                        .svg
                                    </a>{" "}
                                    <a
                                        href={`http://localhost:4000/images/brand-logo/brandkit-forbit/forbitspace/PNG/Logo-forbitspace-brandkit-${item.number}.png`}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                    >
                                        .png
                                    </a>
                                </LinkImg>
                            </BrandLogo>
                        ))}
                    </WrapperLogo>
                </Content>
                <Content>
                    <Title>Forbitspace Noise Logo</Title>
                    <WrapperLogo>
                        {forbitspaceLogoNoise.map((item, index) => (
                            <BrandLogo>
                                <BackgroundImg>
                                    <img
                                        src="../images/contributor-page/orbit-background.svg"
                                        alt=""
                                    />
                                </BackgroundImg>
                                <LogoImg>
                                    <img
                                        src={`../images/brand-logo/brandkit-forbit/forbitspace-noise/PNG/Logo-forbitspace-noise-${item.number}.png`}
                                        alt=""
                                    />
                                </LogoImg>
                                <LinkImg>
                                    <a
                                        href={`http://localhost:4000/images/brand-logo/brandkit-forbit/forbitspace-noise/PNG/Logo-forbitspace-brandkit-${item.number}.png`}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                    >
                                        .png
                                    </a>
                                </LinkImg>
                            </BrandLogo>
                        ))}
                    </WrapperLogo>
                </Content>
                <Content>
                    <Title>Horizontal Logo</Title>
                    <WrapperLogo>
                        <BrandLogo>
                            <BackgroundImg>
                                <img
                                    src="../images/contributor-page/orbit-background.svg"
                                    alt=""
                                />
                            </BackgroundImg>
                            <LogoImg>
                                <img
                                    src="../images/logo/logo-forbitspace-dark.png"
                                    alt=""
                                />
                            </LogoImg>
                            <LinkImg>
                                <a href="/">.svg</a>{" "}
                                <a
                                    href="http://localhost:4000/images/logo/logo-forbitspace-dark.png"
                                    target="_blank"
                                    rel="noopener noreferrer"
                                >
                                    .png
                                </a>
                            </LinkImg>
                        </BrandLogo>
                        <BrandLogo>
                            <BackgroundImg>
                                <img
                                    src="../images/contributor-page/orbit-background.svg"
                                    alt=""
                                />
                            </BackgroundImg>
                            <LogoImg>
                                <img
                                    src="../images/logo/logo-forbitspace-dark.png"
                                    alt=""
                                />
                            </LogoImg>
                            <LinkImg>
                                <a href="/">.svg</a> <a href="/">.png</a>
                            </LinkImg>
                        </BrandLogo>
                        <BrandLogo>
                            <BackgroundImg>
                                <img
                                    src="../images/contributor-page/orbit-background.svg"
                                    alt=""
                                />
                            </BackgroundImg>
                            <LogoImg>
                                <img
                                    src="../images/logo/logo-forbitspace-dark.png"
                                    alt=""
                                />
                            </LogoImg>
                            <LinkImg>
                                <a href="/">.svg</a> <a href="/">.png</a>
                            </LinkImg>
                        </BrandLogo>
                    </WrapperLogo>
                </Content>
                <Content>
                    <DownloadButton>
                        <BackgroundImg>
                            <img
                                src="../images/contributor-page/orbit-background.svg"
                                alt=""
                            />
                        </BackgroundImg>
                        <a
                            href="../images/brand-logo/brandkit-forbit.zip"
                            download="brandkit-forbit"
                        >
                            Download Full
                        </a>
                    </DownloadButton>
                </Content>
            </WrapContainer>
        </BoxSection>
    );
};

const BoxSection = styled.section`
    position: relative;
    padding: 8vh 0 0;
    margin: 0 0 2rem;
    @media (max-width: 770px) {
        padding-top: 60px;
        margin: 0 0 1rem;
    }
`;
const WrapContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    gap: 20px;
    @media (max-width: 768px) {
        /* flex-direction: column-reverse; */
    }
`;
const WrapImg = styled.div`
    /* width: 40%; */
    /* max-width: 450px; */
    @media (max-width: 768px) {
        /* width: 50%; */
    }
`;
const Content = styled.div``;
const Title = styled.div`
    font-size: 28px;
    font-weight: 200;
`;
const WrapperLogo = styled.div`
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-wrap: wrap;
    gap: 2rem;
    @media (max-width: 576px) {
        flex-direction: column;
    }
`;
const BrandLogo = styled.div`
    max-width: 450px;
    width: 30%;
    /* background-color: #0c3d68; */

    padding: 1rem;
    border-radius: 16px;
    display: flex;
    align-items: center;
    justify-content: center;

    position: relative;
    @media (max-width: 576px) {
        width: 100%;
    }
`;
const LogoImg = styled.div`
    width: 55%;
    max-width: 250px;
    position: absolute;
`;
const BackgroundImg = styled.div`
    opacity: 0.45;
`;
const LinkImg = styled.div`
    position: absolute;
    bottom: 5rem;
    a {
        text-decoration: none;
        color: #fff;
    }
`;
const DownloadButton = styled.div`
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    img {
        width: 100%;
        max-width: 250px;
        max-height: 100px;
    }
    a {
        position: absolute;
        text-decoration: none;
        color: #fff;
        text-transform: uppercase;
    }
`;
export default Brands;
