import styled from "styled-components";
import { DataSocialLinkFooter, EcosystemData } from "../constants";
const System = () => {
  const ContributorRender = ({ data, title }) => {
    return (
      <WrapperInner>
        <div className="header">{title}</div>
        <div className="img-content">
          {data.map((item, index) => (
            <div className="img-holder" key={index}>
              {/* <a href="/#"> */}
              <img
                src={`./images/logo-contributor/${item.img}.svg`}
                alt={`logo-${item.img}`}
              />
              {/* </a> */}
            </div>
          ))}
        </div>
      </WrapperInner>
    );
  };

  return (
    <WrapperSystem>
      <div className="container">
        <ContributorRender data={EcosystemData} title={"Ecosystem partners"} />
        {/* <ContributorRender data={ContributorData} title={"Stakeholders"} /> */}
        <BecomeBox>
          <div className="content">
            <div className="header-content">Join the community</div>
            <div className="inner-content">
              Learn more about Maker, chat with the team, others in the
              community,
              <br />
              and have your say in shaping the future of decentralized finance.
            </div>
          </div>
        </BecomeBox>{" "}
        <SocialLink>
          {DataSocialLinkFooter.map((item, index) => {
            return (
              <Link href={item.link} target="_blank" key={index}>
                <Icon src={item.url} alt="icon social media"></Icon>
              </Link>
            );
          })}
        </SocialLink>
      </div>
    </WrapperSystem>
  );
};

const WrapperSystem = styled.section`
  @media (min-width: 1440px) {
    max-width: 1320px;
    margin: 0 auto;
  }
  @media (min-width: 768px) {
    padding: 45px 0;
  }
`;
const WrapperInner = styled.div`
  .header {
    font-size: 2rem;
    font-weight: 500;
    line-height: 1.5;
    text-align: center;
    color: #fff;

    @media (max-width: 415px) {
      font-size: 26px;
    }
  }
  .img-content {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    padding: 45px;
    gap: 10px;

    @media (max-width: 576px) {
      padding: 30px 10px;
      justify-content: space-between;
    }
  }
  .img-holder {
    width: 19%;
    padding: 25px 0;

    img {
      height: auto;
      width: auto;
    }

    @media (max-width: 945px) {
      width: 22.5%;
    }

    @media (max-width: 768px) {
      width: 32%;
    }

    @media (max-width: 574px) {
      width: 45%;
    }

    /* @media (max-width: 415px) {
      width: 70%;
    } */
  }
`;

const BecomeBox = styled.div`
  .content {
    text-align: center;
  }
  .header-content {
    font-size: 2rem;
    font-weight: 500;
  }
  .inner-content {
    font-size: 22px;
  }
  @media (max-width: 786px) {
    .header-content {
      font-size: 26px;
    }
    .inner-content {
      font-size: 14px;
    }
  }
`;
const SocialLink = styled.div`
  width: 100%;
  text-align: center;
  padding: 30px 0;
`;
const Link = styled.a`
  margin-right: 20px;
  :hover {
    cursor: pointer;
  }
`;

const Icon = styled.img`
  max-width: 30px;
  width: 100%;
  transition: all ease-in-out 0.2s;
  :hover {
    transform: scale(1.2);
  }
  @media (max-width: 576px) {
    max-width: 25px;
  }
`;

export default System;
